const questionToggles=document.querySelectorAll(".questions__toggle"),questionAnswers=document.querySelectorAll(".questions__answer");for(let s of questionAnswers)s.classList.remove("questions__answer--nojs");for(let s of questionToggles)s.classList.remove("questions__toggle--nojs");for(let s=0;s<questionToggles.length;s++)toggleAnswer(questionToggles[s],questionAnswers[s]);function toggleAnswer(s,e){s.addEventListener("click",(function(){s.classList.contains("questions__toggle--closed")?answerOpen(s,e):answerClose(s,e)}))}function answerOpen(s,e){s.classList.remove("questions__toggle--closed"),s.classList.add("questions__toggle--opened"),e.classList.remove("questions__answer--closed"),e.classList.add("questions__answer--opened")}function answerClose(s,e){s.classList.remove("questions__toggle--opened"),s.classList.add("questions__toggle--closed"),e.classList.remove("questions__answer--opened"),e.classList.add("questions__answer--closed")}